import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { toggleMenu } from '../Utils/appSlice';
import { YOUTUBE_SEARCH_API } from '../Utils/constant';
import { cacheResults } from '../Utils/searchSlice';


const Head = () => {
  const [searchQuery,setSearchQuery]=useState("");
  const[suggestions,setSuggestion]=useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const searchCache=useSelector((store)=>store.search);
  const dispatch=useDispatch();
  useEffect(()=>{
   
   const timer=setTimeout(()=> {
    if (searchCache[searchQuery]) {
      setSuggestion(searchCache[searchQuery]);
   }
   else{
   getSearchSuggestion()
   }
  },200);
  
   return ()=>{
    clearTimeout(timer);
   }
  },[searchQuery])
  const getSearchSuggestion=async()=>{
    console.log("Api call-"+searchQuery);
    const data=await fetch(YOUTUBE_SEARCH_API+searchQuery);
    const json=await data.json();
    //console.log(json[1]);
    setSuggestion(json[1]);
    dispatch(cacheResults({
      [searchQuery]: json[1],
    }))
  }
  const toggleMenuHandler=()=>{
    dispatch(toggleMenu());

  };

  return (
   
    <div className="grid grid-flow-col p-1 mt-0 m-2 ml-0 shadow-lg fixed top-0 w-full z-50 bg-white ">
    <div className="flex col-span-3">
    <img onClick={()=>toggleMenuHandler()}
     className ="h-12 cursor-pointer"alt="menu"
    src="data:image/png;base64,"
    />
    <a href="/">
    <img className="h-12 mx-2" alt="logo"
    src="https://w7.pngwing.com/pngs/24/227/png-transparent-youtube-logo-youtube-text-trademark-logo-thumbnail.png"/>
    </a>
    </div>
    <div className="col-span-10 px-2">
    <div>
    <input className="w-1/2 border border-gray-500 p-2 rounded-l-full" type="text" value={searchQuery} onChange={(e)=>setSearchQuery(e.target.value)}
    onFocus={() => setShowSuggestions(true)}
    onBlur={() => setShowSuggestions(false)}
    />
    <button className="border border-gray-600 py-2 px-5 bg-gray-100 rounded-r-full">🔍</button>
    </div>
   {showSuggestions &&( <div className="fixed bg-white py-2 px-5 w-[37rem] shadow-lg rounded-lg border border-gray-100  ">
    <ul>
    {suggestions.map(s=>(<li key={s} className='py-2 shadow-sm hover:bg-gray-100'> 🔍 {s}</li>))}
    
    </ul>
    </div>
   )}
    </div>
    <div className="col-span-1">
    <img className="h-12"alt="user icon"
    src="data:image/png;base64,"
    />
    </div>
    </div>
  )
}

export default Head